import React, { useState } from 'react';
import './App.css';
import './key.js';
import Axios from 'axios';
import RecipeTile from './RecipeTile';


function App() {
  const [query, setQuery] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [healthlabel, sethealthlabel] = useState("all");
  const [meallabel, setmeallabel] = useState("all");

  const YOUR_APP_ID = "70c35a79";
  const YOUR_APP_KEY = "e636f7e338545e8b405e86cfc465aaf3";  

var url = `https://api.edamam.com/search?q=${query}&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}&from=0&to=100&health=${healthlabel}&mealType=${meallabel}`;

async function getRecipes() {
  var result = await Axios.get(url);
  setRecipes(result.data.hits);
  console.log(result.data);
}

const submit = (e) => {
  e.preventDefault();
  getRecipes();
}

  return (
    <div className="App">
      <h1 onClick={getRecipes}>Food Recipe Search</h1>
      <form onSubmit={submit}>
        <input type="text" name="search" placeholder="Search for a ingredient..." value={query} onChange={(e) => setQuery(e.target.value)}/>
        <input type="submit" value="Search"/>
        <select>
          <option onClick={() =>  sethealthlabel("")} value="">All</option>
          <option onClick={() =>  sethealthlabel("vegan")} value="vegan">Vegan</option>
          <option onClick={() =>  sethealthlabel("vegetarian")} value="vegetarian">Vegetarian</option>
          <option onClick={() =>  sethealthlabel("gulten-free")} value="gulten-free">Gluten Free</option>
          <option onClick={() =>  sethealthlabel("shellfish-free")} value="shellfish-free">Shellfish Free</option>
          <option onClick={() =>  sethealthlabel("peanut-free")} value="peanut-free">Peanut Free</option>
          <option onClick={() =>  sethealthlabel("fish-free")} value="fish-free">Fish Free</option>
          <option onClick={() =>  sethealthlabel("dairy-free")} value="dairy-free">Dairy Free</option>
          <option onClick={() =>  sethealthlabel("low-sugar")} value="low-sugar">Low Sugar</option>
          <option onClick={() =>  sethealthlabel("egg-free")} value="egg-free">Egg Free</option>
        </select>

        <select>
          <option onClick={() =>  setmeallabel("")} value="">All</option>
          <option onClick={() =>  setmeallabel("breakfast")} value="vegan">Breakfast</option>
          <option onClick={() =>  setmeallabel("lunch")} value="vegetarian">Lunch</option>
          <option onClick={() =>  setmeallabel("dinner")} value="gulten-free">Dinner</option>
          <option onClick={() =>  setmeallabel("snack")} value="shellfish-free">Snack</option>
        </select>
      </form>

      <div>
        {recipes.map((recipe) => {
          return <RecipeTile recipe={recipe} />;
        })}

      </div>
    </div>
  );
}

export default App;
