import React, { useState } from 'react';
import './App.css';
import './key.js';
import Axious from 'axios';
import RecipeTile from './RecipeTile';


function App() {
  const [query, setQuery] = useState("");
  const [recipes, setRecipes] = useState([]);

  const YOUR_APP_ID = "70c35a79";
  const YOUR_APP_KEY = "e636f7e338545e8b405e86cfc465aaf3";  

var url = `https://api.edamam.com/search?q=${query}&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}&from=0&to=3&calories=591-722&health=alcohol-free`;

async function getRecipes() {
  var result = await Axious.get(url);
  setRecipes(result.data.hits);
  console.log(result.data);
}

const submit = (e) => {
  e.preventDefault();
  getRecipes();
}

  return (
    <div className="App">
      <h1 onClick={getRecipes}>Food Recipe Search</h1>
      <form onSubmit={submit}>
        <input type="text" name="search" placeholder="Search for a ingredient..." value={query} onChange={(e) => setQuery(e.target.value)}/>
        <input type="submit" value="Search"/>
      </form>

      <div>
        {recipes.map((recipe) => {
          return <RecipeTile/>
        })}

      </div>
    </div>
  );
}

export default App;
